usage.basic:
import { DataTable } from '@/components/DataTable';

const columns = [
  { key: 'name', header: 'Name' },
  { key: 'email', header: 'Email' }
];

const data = [
  { name: 'John Doe', email: 'john@example.com' }
];

<DataTable columns={columns} data={data} />

usage.advanced:
import { DataTable } from '@/components/DataTable';

const columns = [
  {
    key: 'name',
    header: 'Name',
    sortable: true,
    filterable: true
  },
  {
    key: 'status',
    header: 'Status',
    cell: (value) => <Badge variant={value}>{value}</Badge>
  }
];

<DataTable
  columns={columns}
  data={data}
  searchable={true}
  exportable={true}
  selectable={true}
  paginated={true}
/>
